"""
binarysearch.com :: Maximize Roster Rating
jramaswami
"""


import math
import collections


Player = collections.namedtuple('Player', ['age', 'rating'])


class Solution:

    def solve(self, ratings, ages):

        players = [Player._make(t) for t in zip(ages, ratings)]
        players.sort()

        # D[max_rating] = max team rating
        curr_team = collections.defaultdict(int)
        curr_team[0] = 0
        next_team = collections.defaultdict(int)
        for player in players:
            for max_rating, max_team_rating in curr_team.items():
                if player.rating >= max_rating:
                    next_team[player.rating] = max(
                            next_team[player.rating],
                            max_team_rating + player.rating
                    )
                next_team[max_rating] = max(
                        next_team[max_rating],
                        max_team_rating
                )
            curr_team, next_team = next_team, collections.defaultdict(int)

        return max(curr_team.values())


def test_1():
    ratings = [5, 4, 8]
    ages = [25, 24, 18]
    assert Solution().solve(ratings, ages) == 9


def test_2():
    "WA"
    ratings = [1, 1]
    ages = [1, 0]
    assert Solution().solve(ratings, ages) == 2


